sysauto_script.script:
var gr = new GlideRecord('x_458834_car_reser_vehicle_request');
var pickUpLeadTime = gs.getProperty('x_458834_car_reser.pickup.leadtime', 24) * 60 * 60 * 1000;

var noticeTime = new GlideDateTime();
noticeTime.add(pickUpLeadTime);


gr.addEncodedQuery("start_date<=javascript:gs.dateGenerate(noticeTime.getDate(),'start')^request_state=16^pick_up_reminderISEMPTY");
gr.query();

var rightNow = new GlideDateTime();
gs.info(noticeTime.getDate());

while (gr.next()) {
	gr.pick_up_reminder = rightNow;
	gr.work_notes = noticeTime + ' :Sent Email Notification! - Vehicle is ready for pickup!';
	gr.update();
	gs.eventQueue('x_458834_car_reser.pickUp', gr, gr.number, gs.getUserName());
}
